import React from 'react';
import './sign-in.style.scss';




class SignIn extends React.Component{
    constructor(props){
        super(props);

        this.state={
            email: '',
            password: ''
        }
    }

    // preventing the default action of the submit form and setting email and password back to empty
    handelSubmit = event =>{
        event.preventDefault();

        this.setState({email: '', password:''})
    };
    // this is to dynamically get the name and value from the form input items
    handelChange = event =>{
        const {value,name} = event.target;

        this.setState({[name]: name});
    };
    render(){
        return(
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>

                <form onSubmit={this.handelSubmit}>
                    <input type='email' name='email' value ={this.state.email} onChange={this.handelChange} required />
                    <label >Email</label>
                    <input type='password' name='password' value ={this.state.password} onChange={this.handelChange} required />
                    <label >Password</label>

                    <input type='submit' value='Submit Form'/>
                </form>
            </div>
        )
    }
    
}

export default SignIn;
